import React, { useState } from 'react';

const Display = ({text, stat}) => {
  return (
    <div>
      <p>{text}: {stat}</p>
    </div>
  );
}

const Button = ({text, handleClick}) => {
  return (
    <button onClick={handleClick}>{text}</button>
  );
}

const Statistics = ({good, neutral, bad}) => {
  const totalFeedback = good + bad + neutral;
  const avgScore = (good - bad) / totalFeedback;
  const percentPositive = good * 100 / totalFeedback;

  return (
    <div>
      <Display text="Good" stat={good} />
      <Display text="Neutral" stat={neutral} />
      <Display text="Bad" stat={bad} />
      <Display text="Total Feedback" stat={totalFeedback} />
      <Display text="Average Score" stat={avgScore} />
      <Display text="Percent Positive Feedback" stat={percentPositive + "%"} />
    </div>
  );
}

const App = () => {
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);


  return (
    <div>
      <h1>Give Feedback</h1>
      <Button text="Good" handleClick={() => setGood(good + 1)} />
      <Button text="Neutral" handleClick={() => setNeutral(neutral + 1)} />
      <Button text="Bad" handleClick={() => setBad(bad + 1)} />
      <h1>Statistics</h1>
      <Statistics good={good} neutral={neutral} bad={bad} />
    </div>
  );
}

export default App;
